import React, { useState, useEffect } from "react";
import { signInWithEmailAndPassword, onAuthStateChanged, createUserWithEmailAndPassword } from "firebase/auth";
import { auth } from "../firebase.js";
import { useNavigate } from "react-router-dom";

function Welcome() {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [isRegistering, setIsRegistering] = useState(false);
    const[registerInformation,setRegisterInformation]=useState({
        email:"",
        confirmEmail:"",
        password:"",
        confirmPassword:""
    })

    useEffect(() => {
        auth.onAuthStateChanged((user) => {
            if (user) {
                navigate("/Home");
            }
        });
    }, []);
    const navigate = useNavigate();

    const handleEmailChange = (e) => {
        setEmail(e.target.value);
    };

    const handlePasswordChange = (e) => {
        setPassword(e.target.value);
    };
    const handleSignIn = () => {
        signInWithEmailAndPassword(auth, email, password)
            .then(() => {
                navigate("/Home");
            })
            .catch((err) => alert(err.message));
    };
    const handleRegister=()=>{
        createUserWithEmailAndPassword(auth,email.password).then(()=>{
            navigate("/Home");
        }).catch((err)=>alert(err.message));
    };

    return (
        <div className="welcome">
            <h1>Todo List</h1>
            <div className="login_register_container">
                {isRegistering ? (
                    <>
                        <input type="email" placeholder="Email" value={registerInformation.email} onChange={()=>setRegisterInformation({
                            ...registerInformation.email=e.target.value
                        })}/>
                        <input type="email" placeholder="Confirm email" value={registerInformation.confirmEmail} />
                        <input type="password" placeholder="password" />
                        <input type="password" placeholder="Confirm password" />

                        <button onClick={handleRegister}>Register</button>
                        <button onClick={() => setIsRegistering(false)}>
                            go back
                        </button>
                    </>
                ) : (
                    <>
                        <input
                            type="email"
                            onChange={handleEmailChange}
                            value={email}
                        />
                        <input
                            type="password"
                            onChange={handlePasswordChange}
                            value={password}
                        />
                        <button onClick={handleSignIn}>Sign in</button>
                        <button onClick={() => setIsRegistering(true)}>
                            Create an account
                        </button>
                    </>
                )}
            </div>
        </div>
    );
}

export default Welcome;
